SELECT COUNT(DISTINCT CONCAT(c.first_name ," ", c.last_name)) as customer_namesFROM customer as cINNER JOIN rental as r on r.customer_id = c.customer_id and c.active>=1INNER JOIN inventory as i on i.inventory_id=r.inventory_idINNER JOIN film_category as t on t.film_id = i.film_idINNER JOIN category as cat on (cat.category_id = t.category_id) and cat.name = "ACTION"ORDER BY  c.first_name;