SELECT COUNT(DISTINCT (c.customer_id) ) as Customers_Action_butnot_Horror FROM customer as cINNER JOIN rental as r on r.customer_id = c.customer_idINNER JOIN inventory as i on i.inventory_id=r.inventory_idINNER JOIN film_category as t on t.film_id = i.film_idINNER JOIN category as cat on (cat.category_id = t.category_id) and cat.name = "Action"AND r.customer_id NOT IN(SELECT c.customer_id FROM customer as cINNER JOIN rental as r on r.customer_id = c.customer_idINNER JOIN inventory as i on i.inventory_id=r.inventory_idINNER JOIN film_category as t on t.film_id = i.film_idINNER JOIN category as cat on (cat.category_id = t.category_id) and cat.name = "Horror");